/*timer 2 usato per l'emissione dei suoni inizializzato e abilitato da timer 3 con diversi valori di k in base alla nota scelta*/
void TIMER2_IRQHandler (void)
{		//timer2
	static int ticks=24;
	int var;
	/* DAC management*/
	var = (SinTable[ticks] * volume)/(3); // volume/3 fattore di moltiplicazione per la gestione del valore dell'audio
	LPC_DAC->DACR = var<<6;
	ticks++;
	if(ticks==45)
		ticks=0;
	LPC_TIM2->IR = 1;			// clear interrupt flag
  return;
}

/*timer 3 settato a 250ms per abilitare e disabiltare il timer 2 in modo da far suonare le note in questo determinato intervallo*/
//volendo si poteva utilizzare una valore di settaggio minore con dei contatori interni per gestire i vari tipi di nota
void TIMER3_IRQHandler (void)
{		//timer3
	disable_timer(2);
	reset_timer(2);
	/*melodia snack e meal*/
	if((pasto || merenda)&&pos!=-1){
		if(pos==1)
			pos++;
		else if(pos==5){
			pos=-1;
			disable_timer(2);
			reset_timer(2);
		}else{
			init_timer(2,freq1[pos]);
			enable_timer(2);
			pos++;
		}
	}
	/*melodia della morte*/
	if((!vivo)&&pos!=-1){
		if(pos==5){
			pos=-1;
			disable_timer(2);
			reset_timer(2);
		}else{
			init_timer(2,freq2[pos]);
			enable_timer(2);
			pos++;
		}
	}
	/*melodia delle cuddles*/
	if((cuore)&&pos!=-1){
		if(pos==6){
			pos=-1;
			disable_timer(2);
			reset_timer(2);
		}else{
			init_timer(2,freq3[pos]);
			enable_timer(2);
			pos++;
		}
	}
	
	LPC_TIM3->IR = 1;			// clear interrupt flag
  return;
}


